function getMorePickup(i,t){$.ajax({type:"Get",dataType:"json",url:"/GetPickup.ashx",data:"start="+i+"&count="+t,success:function(i){i.length<t&&($("#btn-more").hide(),$("#pickup-end").css("display","block")),$.each(i,function(i,t){var e=$('<div class="pickup-list-item">   <div class="item-block">       <div class="item-title">           <a href="pickupDetail.aspx?id='+t.PickupID+'" class="item-title-icon"><img src="'+t.PickupCommendItemIcon+'" alt="'+t.PickupCommendItem+'" title="'+t.PickupCommendItem+'"></a>           <div class="item-title-text"><a href="pickupDetail.aspx?id='+t.PickupID+'">'+t.PickupCommendItem+'</a></div>       </div>       <div class="item-block-content">           <div class="block-content-text"><a href="pickupDetail.aspx?id='+t.PickupID+'"><p>'+t.PickupCommendContent+'</p></a><i class="my-icon icon-quote-left"></i><i class="my-icon icon-quote-right"></i></div>           <p class="block-content-uploadtime">'+t.PickupUploadTime+"</p>       </div>   </div></div>");$("#pickup-list").append(e).masonry().masonry("appended",e)})},error:function(i,t,e){$("#btn-more").hide(),$("#pickup-end").css("display","block")}})}$(function(){}),$(document).ready(function(){var i=0,t=6,e=$("#pickup-list");e.masonry({itemSelector:".pickup-list-item",isAnimated:!0}),getMorePickup(i,t),$("#btn-more").click(function(){i+=t,getMorePickup(i,t)})});